import { Accordion, Table } from '~/shared';
import { type AccordionItem } from '~/shared';

type PolicyContentBlock = string | string[];

interface PolicySection {
  number: string;
  title: string;
  content: PolicyContentBlock[];
}

const mapPolicyToAccordion = (sections: PolicySection[]): AccordionItem[] => {
    return sections.map((section) => {
        const htmlContent = section.content.map((block) => {
            if (Array.isArray(block)) {
                const listItems = block.map((li) => `<li>${li}</li>`).join('');
                return `<ul>${listItems}</ul>`;
            }
            return `<p>${block}</p>`;
        }).join('');

        return {
            number: section.number,
            question: section.title,
            content: htmlContent,
        };
    });
};

